
public class Homewokr_array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//byte 5개 짜리 배열 선언, 메모리 할당, 적당한 값(1,2,3,4,5) 할당
		byte [] arrByte;
		arrByte=new byte[6];
		arrByte=new byte[] {1,2,3,4,5};
		
//		char 5개 짜리 배열 선언, 메모리 할당하고, 적당한 값(A,B,C,D,E)을 할당
		char[] arrChar;
		arrChar=new char[6];
		arrChar=new char[] {'A','B','C','D','E'};
		
//		boolean, 5개 짜리 배열 선언, 메모리 할당, 적당한 값(false, true, flase, true, flase)
		boolean[] arrBoolean;
		arrBoolean= new boolean[6];
		arrBoolean= new boolean[] {false, true, false, true, false};
//		short 5개 짜리 배열 선언, 메모리 할당, 적당한 값(100, 200, 300, 400, 500)할당
		short[] arrShort;
		arrShort= new short[6];
		arrShort= new short[] {100,200,300,400,500};

//		long 5개 짜리 배열 선언, 메모리 할당, 적당한 값(90, 135, 180, 225, 270)
		long [] arrLong;
		arrLong= new long[6];
		arrLong= new long[] {90, 135, 180, 225, 270};
		
//		float 5개 짜리 배열 선언, 메모리 할당, 적당한 값(3.14 / 6.28 / 9.42/12.56/15.700001)을 할당
		float[] arrFloat;
		arrFloat= new float[6];
		arrFloat= new float[] {3.14f, 6.28f, 9.42f, 12.56f, 15.700001f};
		
//		double 5개 짜리 배열 선언, 메모리 할당, 적당한 값 (3.141592, 3.141592, 3.141592, 3.141592, 3.141592)
		double[] arrDouble;
		arrDouble= new double[6];
		arrDouble= new double[] {3.141592, 3.141592, 3.141592, 3.141592, 3.141592};
		
	}

}
